\section*{\color{myblue}5. Exercises}

\subsection*{Level 1: Basic Understanding}
\begin{enumerate}[label=\textbf{Q1.}]
    \item Given the function $f(x,y) = 5x^4 y^3 - 2x^2 + 7y$, find the partial derivatives $\frac{\partial f}{\partial x}$ and $\frac{\partial f}{\partial y}$.
\end{enumerate}

\subsubsection*{Solution to Q1:}
To find $\frac{\partial f}{\partial x}$, treat $y$ as a constant:
$$ \frac{\partial f}{\partial x} = \frac{\partial}{\partial x} (5x^4 y^3 - 2x^2 + 7y) = 5(4x^3)y^3 - 2(2x) + 0 = 20x^3 y^3 - 4x $$
To find $\frac{\partial f}{\partial y}$, treat $x$ as a constant:
$$ \frac{\partial f}{\partial y} = \frac{\partial}{\partial y} (5x^4 y^3 - 2x^2 + 7y) = 5x^4 (3y^2) - 0 + 7 = 15x^4 y^2 + 7 $$

\subsection*{Level 2: Intermediate Application}
\begin{enumerate}[label=\textbf{Q2.}]
    \item Find the gradient of the function $g(x,y,z) = x \cos(y) + y e^z$ at the point $(1, \pi/2, 0)$.
\end{enumerate}

\subsubsection*{Solution to Q2:}
First, we find the partial derivatives:
$\frac{\partial g}{\partial x} = \cos(y)$, $\frac{\partial g}{\partial y} = -x\sin(y) + e^z$, and $\frac{\partial g}{\partial z} = ye^z$.
Then, we evaluate these at the point $(1, \pi/2, 0)$:
$\frac{\partial g}{\partial x}(1, \pi/2, 0) = \cos(\pi/2) = 0$,
$\frac{\partial g}{\partial y}(1, \pi/2, 0) = -1\sin(\pi/2) + e^0 = -1 + 1 = 0$,
$\frac{\partial g}{\partial z}(1, \pi/2, 0) = (\pi/2)e^0 = \pi/2$.
Therefore, the gradient at $(1, \pi/2, 0)$ is $\nabla g(1, \pi/2, 0) = \langle 0, 0, \pi/2 \rangle$.

\subsection*{Level 3: Advanced Conceptualization}
\begin{enumerate}[label=\textbf{Q3.}]
    \item Set up, but do not evaluate, a double integral in Cartesian coordinates to find the volume under the surface $z = \sqrt{4 - x^2 - y^2}$ and above the region $R$ in the $xy$-plane defined by $x^2+y^2 \le 4$.
\end{enumerate}

\subsubsection*{Solution to Q3:}
The volume $V$ is given by the double integral of the function $z = \sqrt{4 - x^2 - y^2}$ over the region $R$.  The region $R$ is a disk of radius 2 centered at the origin.  Therefore, the double integral is:
$$ V = \iint_R \sqrt{4 - x^2 - y^2} \,dA = \int_{-2}^{2} \int_{-\sqrt{4-x^2}}^{\sqrt{4-x^2}} \sqrt{4 - x^2 - y^2} \,dy\,dx $$
